class Traversal:
  def __init__(self):
    self.apiRequest = ApiRequest()

  '''
  searchTweets function to make a search request to the twitter api using tweepy.
  @param: search String hashtag used for the search.
  @param: state String name of the state.
  @param: geocode String geocode of the location in a state.
  @param: max_id  String id of the last tweet fetched from the previous search request.
  '''
  def searchHashTagTweets(self, search, max_id=None):
      global HASHTAGS_FETCHED
      if HASHTAGS_FETCHED<6:
        HASHTAGS_FETCHED+=1  
        tweets = self.apiRequest.getApiRequest().search(q=search, count=200)
        if len(tweets) != 0:
          self.addTweets(tweets, category='general', sub_category='na', state_name='na',
                         user=None, max_id=max_id, search=search)
        else:
          print("No tweets found!")
      else: HASHTAGS_FETCHED=0


  def getHashTags(self, entities):
    if 'hashtags' in entities:
      return entities['hashtags']


  def addHashTagsTweetsPublic(self, tweets):
    for tweet in tweets:
      hashtags = self.getHashTags(tweet.entities)
      try:
        for hashtag in hashtags:
          searchHashTagTweets(hashtag['text'])
      except:
        pass

  def getTweetId(self, tweet):
    return str(tweet.id_str)

  def getRetweetStatus(self, tweet):
     return str(tweet.retweeted)

  def getRetweetCount(self, tweet):
    return str(tweet.retweet_count)

  def getTweetText(self, tweet):
    return str(tweet.text)

  def getTweetDate(self, tweet):
    ts = time.strftime('%Y-%m-%d %H:%M:%S',
                       time.strptime(str(tweet.created_at),
                       '%Y-%m-%d %H:%M:%S'))
    ts = datetime.datetime.strptime(ts, "%Y-%m-%d %H:%M:%S")
    epoch = datetime.datetime.utcfromtimestamp(0)
    delta = ts - epoch
    total_seconds = delta.days*86400+delta.seconds+delta.microseconds/1e6
    milli_seconds = total_seconds*1000
    return int(milli_seconds)

  def getTweetUser(self, tweet):
    return str(tweet.user.screen_name)

  def getUserFollowersCount(self, tweet):
    return str(tweet.user.followers_count)

  def getUserFriendsCount(self, tweet):
    return str(tweet.user.friends_count)

  
  def getTweetHashtags(self, tweet):
    hashtags = []
    if 'hashtags' in tweet.entities:
      for h in tweet.entities['hashtags']:
        hashtags.append(h['text'])
    return ','.join(hashtags)

  def getTweetUrls(self, tweet):
    urls = []
    if 'urls' in tweet.entities:
      for u in tweet.entities['urls']:
        urls.append(u['expanded_url'])
    return ','.join(urls)

  def getTweetUserMentioned(self, tweet):
    users = []
    if 'user_mentions' in tweet.entities:
      for u in tweet.entities['user_mentions']:
        if u['screen_name'] is not None:
          users.append(u['screen_name'])
    return ','.join(users)

  def getTweetYear(self, tweet):
    ts = time.strftime('%Y-%m-%d %H:%M:%S',
                       time.strptime(str(tweet.created_at),
                       '%Y-%m-%d %H:%M:%S'))

    ts = datetime.datetime.strptime(ts, "%Y-%m-%d %H:%M:%S")
    return str(ts.year)

  def getTweetDay(self, tweet):
    ts = time.strftime('%Y-%m-%d %H:%M:%S',
                       time.strptime(str(tweet.created_at),
                       '%Y-%m-%d %H:%M:%S'))

    ts = datetime.datetime.strptime(ts, "%Y-%m-%d %H:%M:%S")
    return str(ts.day)

  def getTweetMonth(self, tweet):
    ts = time.strftime('%Y-%m-%d %H:%M:%S',
                       time.strptime(str(tweet.created_at),
                       '%Y-%m-%d %H:%M:%S'))

    ts = datetime.datetime.strptime(ts, "%Y-%m-%d %H:%M:%S")
    return str(ts.month)


