class Echo(object):
    """An object that implements just the write method of the file-like
    interface.
    """
    def write(self, value):
        """Write the value by returning it, instead of storing in a buffer."""
        logging.info("Echo write function called!")
        return value

def getcsv(request):
    """A view that streams a large CSV file."""
    #rows = ProfileTweets.all().fetch(10)
    logging.info("get csv!")
    q = db.Query(ProfileTweets)
    #q = ProfileTweets.all()
    #rows = q.filter("year =", '2016').fetch(1000)
    #rows = q.fetch(100000)
    rows = q.filter("year =", "2016")
    pseudo_buffer = Echo()
    writer = csv.writer(pseudo_buffer)
    '''rows_list = ['text','created_at', 'state', 'category', 'sub_category',
                 'tweet_id', 'retweet_status', 'retweet_count', 'user_screen_name',
                 'user_name', 'followers_count', 'friends_count', 'hashtags',
                 'urls', 'user_mentioned', 'year', 'day', 'month', 'monthId', 'dayId']
    '''
    logging.info("writing output!")
    response = StreamingHttpResponse((writer.writerow([row.text.decode('utf-8').strip(),row.created_at, row.state, row.category, row.sub_category, 
                                                       row.tweet_id, row.retweet_status, row.retweet_count,
                                                       row.user_screen_name, 
                                                       row.user_name,
                                                       row.followers_count, row.friends_count,
                                                       row.hashtags,
                                                       row.urls.decode('utf-8').strip(), 
                                                       row.user_mentioned.decode('utf-8').strip(),
                                                       row.year, row.day, row.month, row.monthId, row.dayId]) \
    for row in rows if row is not None and row.text is not None and row.urls is not None and row.user_mentioned is not None),
                                     content_type="text/csv")
    logging.info("csv generated!")
    response['Content-Disposition'] = 'attachment; filename="somefilename_2009.csv"'
    return response


